-- 資料表結構 `mountain_weather`
--

CREATE TABLE `mountain_weather` (
  `m_id` varchar(4) NOT NULL,
  `mountain_name` varchar(13) CHARACTER SET utf8 DEFAULT NULL,
  `tem` varchar(9) CHARACTER SET utf8 DEFAULT NULL,
  `high_tem` varchar(8) CHARACTER SET utf8 DEFAULT NULL,
  `low_tem` varchar(7) CHARACTER SET utf8 DEFAULT NULL,
  `wind` varchar(6) CHARACTER SET utf8 DEFAULT NULL,
